(352, 79, 'content_section_2', '<section class="parallax-bg" data-type="background" data-speed="10" id="tours"> \r\n                <div class="wrap">\r\n                    <h3>Book a Running Tour</h3>\r\n                    <ul class=''clearfix''>\r\n                        <li>\r\n                            <div class="toursiconBox">\r\n                                <h5>Scheduled</h5>\r\n                                <a href="#">\r\n                                    <img src="images/scheduled-tour-icon.png" title="Scheduled">\r\n                                </a>\r\n                                <h6>Regular route to suit everyone</h6>\r\n                                <p>Mixed group up to 10 people</p>\r\n                            </div>\r\n                        </li>\r\n                        <li>\r\n                            <div class="toursiconBox">\r\n                                <h5>Private</h5>\r\n                                <a href="">\r\n                                    <img src="images/private-tour-icon.png" title="Private">\r\n                                </a>\r\n                                <h6>Tailored to suit you</h6>\r\n                                <p>Your group of 1-10 people</p>\r\n                            </div>\r\n                        </li>\r\n                        <li>\r\n                            <div class="toursiconBox">\r\n                                <h5>Group</h5>\r\n                                <a href="">\r\n                                    <img src="images/group-tour-icon.png" title="Group">\r\n                                </a>\r\n                                <h6>Customised to your group</h6>\r\n                                <p>Your group of 10-100+ people</p>\r\n                            </div>\r\n                        </li>\r\n                        <li>\r\n                            <div class="toursiconBox">\r\n                                <h5>Teambuilding</h5>\r\n                                <a href="">\r\n                                    <img src="images/team-tour-icon.png" title="Teambuilding">\r\n                                </a>\r\n                                <h6>Healthy, fun corporate team bonding</h6>\r\n                                <p>Your team of 10-100+ people</p>\r\n                            </div>\r\n                        </li>\r\n                    </ul>\r\n                </div>\r\n\r\n            </section>\r\n'),
